import React, { useState } from 'react'
import StateParks from './StateParks'
import '../styles/StateButton.css'

function StateButton({ name, abbr }) {
  const [stateParksList, setStateParksList] = useState([])
  const [showStateParks, setShowStateParks] = useState(false)

  async function handleShowParks() {
    await fetch(`https://developer.nps.gov/api/v1/parks?stateCode=${abbr}&limit=50&api_key=hQLUJV4Ctu79CYYc4YTuslyjKz36u5520OCQlXnF`)
      .then((r) => r.json())
      .then((stateParksData) => {
        console.log(stateParksData)
        setStateParksList(stateParksData)
        setShowStateParks(!showStateParks)
        localStorage.setItem('stateParksData', JSON.stringify(stateParksData))
      })
  }

  // const storedData = JSON.parse(localStorage.getItem('stateParksData'));
  // if (storedData) {
  //   setStateParksList(storedData);
  // }

  return (
    <div>
      <button className='button' onClick={handleShowParks}>{name}</button>
      {showStateParks ? <StateParks
        stateParksList={stateParksList} showStateParks={showStateParks} setShowStateParks={setShowStateParks} /> : null}
    </div>
  )
}

export default StateButton
