import { useState, useEffect } from 'react'
import StateButtons from './StateButton'
import StateParks from './StateParks'

function States({ states }) {
  const [showStateParks, setShowStateParks] = useState(false)
  const mappedStates = states.map((state) => {
    return <StateButtons key={state.id} name={state.name} abbr={state.abbreviation} />
  })

  function handleShowParks() {
    setShowStateParks(!showParks)
  }


  return (
    <div className='states'>
      <div className='buttons'>
      {mappedStates}
      </div>
      {showStateParks ? <StateParks /> : null}
    </div>
  )
}

export default States
