import React from 'react'
import '../styles/StateButtons.css'

function StateButtons({ name, abbr }) {

  function handleGetStateParks() {
    fetch(`https://developer.nps.gov/api/v1/parks?stateCode=${abbr}&limit=50&api_key=hQLUJV4Ctu79CYYc4YTuslyjKz36u5520OCQlXnF`)
    .then((r) => r.json())
    .then((stateParksData) => {
      console.log(stateParksData)
    })
  }

  return (
    <div className='buttons'>
      <button className='button' onClick={handleGetStateParks}>{name}</button>
    </div>
  )
}

export default StateButtons
