import { useState, useEffect } from 'react'
import StateButtons from './StateButton'
import StateParks from './StateParks'

function States({ states }) {
  const [stateParks, setStateParks] = useState([])
  const [showStateParks, setShowStateParks] = useState(false)

  function handleShowParks() {
    fetch(`https://developer.nps.gov/api/v1/parks?stateCode=${stateabbr}&limit=50&api_key=hQLUJV4Ctu79CYYc4YTuslyjKz36u5520OCQlXnF`)
    .then((r) => r.json())
    .then((stateParksData) => {
      console.log(stateParksData)
      // setStateParks(stateParksData)
      setShowStateParks(!showStateParks)
    })
  }

  const mappedStates = states.map((state) => {
    return <StateButtons key={state.id} name={state.name} abbr={state.abbreviation} setShowStateParks={setShowStateParks} showStateParks={showStateParks} setStateParks={setStateParks} handleShowParks={handleShowParks}/>
  })

  return (
    <div className='states'>
      <div className='buttons'>
      {mappedStates}
      </div>
      {showStateParks ? <StateParks stateParks={stateParks} /> : null}
    </div>
  )
}

export default States
