import React from 'react'
import StateParks from './StateParks'
import '../styles/StateButton.css'

function StateButton({ name, abbr, showStateParks, setShowStateParks, handleShowParks }) {
  const [stateParks, setStateParks] = useState([])


  function handleShowParks() {
    fetch(`https://developer.nps.gov/api/v1/parks?stateCode=${abbr}&limit=50&api_key=hQLUJV4Ctu79CYYc4YTuslyjKz36u5520OCQlXnF`)
    .then((r) => r.json())
    .then((stateParksData) => {
      console.log(stateParksData)
      // setStateParks(stateParksData)
      setShowStateParks(!showStateParks)
    })
  }

  return (
    <div>
      <button className='button' onClick={handleShowParks}>{name}</button>
      {showStateParks ? <StateParks stateParks={stateParks} /> : null}
    </div>
  )
}

export default StateButton
